<?php

/**
 * FAQsTable
 * 
 * This class has been auto-generated by the Doctrine ORM Framework
 */
class FAQsTable extends Doctrine_Table
{
    /**
     * Returns an instance of this class.
     *
     * @return object FAQsTable
     */
    public static function getInstance()
    {
        return Doctrine_Core::getTable('FAQs');
    }
    public function FAQsListing()
    {
		$qSearch = 	Doctrine_Query::create()
							->from('FAQs fa')
							->where("fa.Globle = ?", "Yes");
							
		return $qSearch;
    }
}